import Layout from '../components/Layout'
import * as React from 'react'

import './blog.scss';
import { IncomingContentful } from '../interfaces';
import { NextFunctionComponent } from 'next';

import { findAllPosts } from '../utils/contentfulUtils';
import Link from 'next/link';


type Props = {
	items: IncomingContentful[]
}

const getAllBlogs = (items: IncomingContentful[]) => {
	return items.map((blog: IncomingContentful, index: number) => {
		return (

			<Link prefetch href={{ pathname: '/post', query: { id: blog.fields.slug } }} as={`/post/${blog.fields.slug}`} >
			{/* // <Link key={index} href={`/post?slug=${blog.fields.slug}`}> */}

				<div
					className='card'

					style={{
						animationDelay: `${index * 0.5}s`,
					}}>
					<h2> {blog.fields.title}</h2>

					{blog.fields.featuredImage && <img src={blog.fields.featuredImage.fields.file.url}
						alt={blog.fields.featuredImage.fields.file.fileName} />}

					<p>
						{blog.fields.description}
						<br />
						<br />
						<strong>Continue reading ....</strong>
					</p>
					<h3>{blog.fields.category}</h3>
				</div>
			</Link>

		);
	});
};

const BlogsPage: NextFunctionComponent<Props> = ({ items }) => {
	return (
		<Layout>
			<div className='blogsContainer'>
				<div className='head'>
					<h3>Sanyam Arya</h3>
					<input
						className='search'
						type='text'
						name='search'
						placeholder='Search..'
					/>
				</div>

				<div className='nav' />
				<div className='main'>{getAllBlogs(items)}</div>
			</div>
		</Layout>
	)
};


BlogsPage.getInitialProps = async () => {
	let items: IncomingContentful[] = await findAllPosts()
	items = [...items, ...items, ...items, ...items];
	return { items }
}

export default BlogsPage;
