import * as React from 'react'
import { NextContext } from 'next'
import Layout from '../components/Layout'
import { ISeo, IncomingContentful } from '../interfaces';
import { findOnePost } from '../utils/contentfulUtils'

type RequestQuery = {
  slug: string,
}

type Props = {
  item: IncomingContentful,
  errors?: string
}

class BlogPostPage extends React.Component<Props> {
  static getInitialProps = async ({ query }: NextContext<RequestQuery>) => {

    try {
      const item = await findOnePost(query.slug);
      return { item }
    } catch (err) {
      return { errors: err.message }
    }
  }

  render() {
    const { item, errors } = this.props;

    if (errors) {
      return (
        <Layout>
          <p><span style={{ color: 'red' }}>Error:</span> {errors}</p>
        </Layout>
      )
    }

    const seoMeta: ISeo = {
      title: item.fields.title,
      description: item.fields.description,
      twitterDescription: item.fields.twitterDescription,
      image: ((item.fields.featuredImage) ? item.fields.featuredImage.fields.file.url : 'https://avatars1.githubusercontent.com/u/28115284?s=460&v=4'),
      keywords: item.fields.keywords,
      type: 'article',
      url: `sanyamarya.io/post?slug=${item.fields.slug}`,
      article:{
        published_on:item.fields.publishedOn,
        section:item.fields.category.toString()
      }
    }
    return (
      <Layout seo={seoMeta}>
        {item.fields.featuredImage && <img src={item.fields.featuredImage.fields.file.url}
          alt={item.fields.featuredImage.fields.file.fileName} />}
        <h1>{item.fields.title}</h1>

      </Layout>
    )
  }
}

export default BlogPostPage
