import Link from 'next/link';
import * as React from 'react';
import './components.scss';
import { INavigation } from '../interfaces';
export interface IProps {
	navLinkList: INavigation[];
}

class NavigationBar extends React.Component<IProps> {
	// constructor(props) {
	// 	super(props);
	// 	if (typeof window === undefined) {
	// 		this.setState({ pathnmae: .pathname });
	// 	}
	// }
	state = {
		pathname: '/'
	}
	public componentDidMount() {
		(window && typeof window !== undefined && window.location && window.location !== undefined) && this.setState({ pathname: window.location.pathname })
	}
	public render() {
		console.log(this.state);

		return <header>{this.getNavLinks(this.props.navLinkList)}</header>;
	}
	public getNavLinks = (navLinkList: INavigation[]) => {
		return navLinkList.map((item: INavigation, index: number) => {
			const CLASS = `fas fa-${item.image}`;
			return (
				// (window || typeof window !== undefined) &&
				<Link key={index} href={item.to} as={item.to}>
					<a
						className={
							this.state.pathname === item.to
								? 'nav-link active'
								: 'nav-link'
						}
					// className='nav-link'
					>
						<span>{item.label}</span>
						<i className={CLASS} />
					</a>
				</Link>
			);
		});
	}
}
export default NavigationBar;
